openItems.rest:
font-family: NationalyzeAlp;
border-radius: 0px;
@include bordered;
@include value-range( 1, 1) {background-color: gold;}
@include value-range( 2, 4) {background-color: silver;}
@include value-range( 5,10) {background-color: red;}
@include value-range(11,22) {background-color: blue;}
@include value-range(23,40) {background-color: orange;}
@include value-range(41,88) {background-color: green;}
@include value-range(89,300) {background-color: purple;}

background-color: black;
border-radius: 0px;
border: solid 2px white;

font-size: 0px;
color: $chance-color;
@include value-range(1, 1) {
  &::before {
    content: "○";
    font-size: 120px;
  }
  transform: translate(-3px, 12px);
}

@include value-range(2, 2) {
  &::before {
    content: "◎";
    vertical-align: middle;
    font-size: 140px;
  }
  transform: translate(0px, 0px);
}

font-size: 0px;
color: $pinch-color;
@include value-range(1, 2) {
  &::before {
    content: "×";
    font-size: 140px;
  }
  transform: translate(0px, -10px);
}

font-size: 0px;
color: lime;
@include value-range(1, 2) {
  &::before {
    content: "×";
    font-size: 140px;
  }
  transform: translate(0px, -10px);
}

closeItems.rest:
font-family: NationalyzeAlp;
border-radius: 0px;
@include bordered;
@include value-range( 1, 1) {background-color: gold;}
@include value-range( 2, 4) {background-color: silver;}
@include value-range( 5,10) {background-color: red;}
@include value-range(11,22) {background-color: blue;}
@include value-range(23,40) {background-color: orange;}
@include value-range(41,88) {background-color: green;}
