actions.action0:
// ○を加算
player.o++;
if (player.combo == 1) {
  player.x++;
}
//combo管理
player.combo = 1;
players.map(function(p) {
  if (p != player) {
    p.combo = 0;
  }
});

setMotion(player, 'o');
addQCount(players, header, property);

// xを減算
player.x--;
// combo管理
player.combo = 0;
setMotion(player, 'x');
addQCount(players, header, property);

judgement:
angular.forEach(players.filter(function(item) {
  /* rankがない人に限定 */
  return (item.rank === 0);
}), function(player, i) {
  /* win条件 */
  if (player.pts >= property.winningPoint) {

    win(player, players, header, property);
    player.combo = 0;
    timerStop();
  }
  /* lose条件 */
  if (player.x <= property.losingPoint) {
    lose(player, players, header, property);
    player.combo = 0;
    timerStop();
  }
});

calc:
angular.forEach(players, function(player, index) {
  // pts
  player.pts = player.o * player.x;
  // pinch, chance
  player.pinch = (player.x == property.losingPoint - 1) && (player.status == 'normal');
  player.chance = ((player.o + 1) * (player.x + player.combo) >= property.winningPoint) &&
    (player.status == 'normal');

  // キーボード入力時の配列の紐付け ローリング等の特殊形式でない場合はこのままでOK\
  player.keyIndex = player.position;
  if (header.pos) {
    player.line = "line1";
  } else {
    player.line = "line2";
  }
});
