actions.action0:
// ○を加算
player.o++;
player.combo = 0;
setMotion(player, 'o');
addQCount(players, header, property);

// ×を加算
player.x++;
player.x += player.combo;
player.combo = 1;
setMotion(player, 'x');
addQCount(players, header, property);

judgement:
angular.forEach(players.filter(function(item) {
  /* rankがない人に限定 */
  return (item.rank === 0);
}), function(player, i) {
  /* win条件 */
  if (player.o >= property.winningPoint) {

    win(player, players, header, property);
    player.combo = 0;
    timerStop();
  }
  /* lose条件 */
  if (player.x >= property.losingPoint) {
    lose(player, players, header, property);
    player.combo = 0;
    timerStop();
  }
});

calc:
angular.forEach(players, function(player, index) {
  // pinch, chance
  player.pinch = (player.x >= property.losingPoint - 1 - player.combo) && (player.status == 'normal');
  player.chance = (player.o + 1 >= property.winningPoint) &&
    (player.status == 'normal');

  // キーボード入力時の配列の紐付け ローリング等の特殊形式でない場合はこのままでOK\
  player.keyIndex = player.position;
  if (header.pos) {
    player.line = "line1";
  } else {
    player.line = "line2";
  }
});
